import React, { useState, useEffect, useCallback, memo } from 'react';
import axios from 'axios';
import { API_CONFIG } from '../config/api.config';

// Repository Item Component
const RepositoryItem = memo(({ repo }) => (
  <div className="repo-item">
    <div className="repo-content">
      <div className="repo-title">
        <img
          src={repo.owner.avatar_url}
          alt={`${repo.owner.login}'s avatar`}
          className="avatar"
          loading="lazy"
        />
        <div>
          <h3>{repo.name}</h3>
          <span className="owner-name">{repo.owner.login}</span>
        </div>
      </div>
      <p className="description">
        {repo.description || 'No description available'}
      </p>
      <div className="repo-stats">
        <span className="stars">‚≠ê {repo.stargazers_count.toLocaleString()}</span>
      </div>
    </div>
  </div>
));

// Footer Component
const Footer = memo(() => (
  <div className="footer-tabs">
    <a href="#trending" className="tab active">
      <span className="tab-icon">‚≠ê</span>
      <span>Trending</span>
    </a>
    <a href="#settings" className="tab">
      <span className="tab-icon">‚öôÔ∏è</span>
      <span>Settings</span>
    </a>
  </div>
));

// Main Component
const StarredRepos = () => {
  const [repositories, setRepositories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const [isLoadingMore, setIsLoadingMore] = useState(false);
  const [error, setError] = useState(null);

  const fetchRepos = async (pageNumber) => {
    try {
      setIsLoadingMore(true);
      const response = await axios.get(
        `${API_CONFIG.BASE_URL}?q=created:>${API_CONFIG.REFERENCE_DATE}&sort=stars&order=desc&page=${pageNumber}&per_page=${API_CONFIG.PER_PAGE}`
      );

      if (pageNumber === 1) {
        setRepositories(response.data.items);
      } else {
        setRepositories((prev) => [...prev, ...response.data.items]);
      }

      setHasMore(response.data.items.length === API_CONFIG.PER_PAGE);
      setLoading(false);
      setIsLoadingMore(false);
    } catch (error) {
      console.error('Error fetching repositories:', error);
      setLoading(false);
      setIsLoadingMore(false);
    }
  };

  const handleScroll = useCallback(() => {
    const { loading, hasMore, isLoadingMore } = state;
    if (window.innerHeight + window.scrollY >= document.documentElement.scrollHeight - 5 
        && !loading && !isLoadingMore && hasMore) {
      setPage(prev => prev + 1);
    }
  }, [state]);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [handleScroll]);

  useEffect(() => {
    fetchRepos(page);
  }, [page]);

  if (loading && page === 1) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <>
      <header className="header">
        <div className="header-content">
          <h1 className="header-title">Trending Repos</h1>
        </div>
      </header>
      
      <div className="repos-container">
        <div className="repos-list">
          {repositories.map(repo => (
            <RepositoryItem key={repo.id} repo={repo} />
          ))}
        </div>

        {isLoadingMore && (
          <div className="pagination-loader">
            <div className="loader-content">
              <span className="loader-icon">üìÑ</span>
              <span>Loading more repositories...</span>
            </div>
          </div>
        )}

        {!hasMore && (
          <div className="pagination-end">
            <div className="end-content">
              <span className="end-icon">üéâ</span>
              <span>You've reached the end!</span>
            </div>
          </div>
        )}

        <div className="footer-tabs">
          <a href="#trending" className="tab active">
            <span className="tab-icon">‚≠ê</span>
            <span>Trending</span>
          </a>
          <a href="#settings" className="tab">
            <span className="tab-icon">‚öôÔ∏è</span>
            <span>Settings</span>
          </a>
        </div>
      </div>
    </>
  );
};

export default StarredRepos;
