import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StarredRepos = () => {
  const [repositories, setRepositories] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRepos = async () => {
      try {
        // Calculate date 10 days ago
        const date = new Date();
        date.setDate(date.getDate() - 10);
        const dateString = date.toISOString().split('T')[0];

        const response = await axios.get(
          `https://api.github.com/search/repositories?q=created:>${dateString}&sort=stars&order=desc`
        );

        setRepositories(response.data.items);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching repositories:', error);
        setLoading(false);
      }
    };

    fetchRepos();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="repos-container">
      <h1>Most Starred GitHub Repositories</h1>
      <h2>(Created in the last 10 days)</h2>
      <div className="repos-list">
        {repositories.map((repo) => (
          <div key={repo.id} className="repo-item">
            <h3>
              <a href={repo.html_url} target="_blank" rel="noopener noreferrer">
                {repo.name}
              </a>
            </h3>
            <p>{repo.description}</p>
            <div className="repo-stats">
              <span>‚≠ê Stars: {repo.stargazers_count}</span>
              <span>üîÑ Forks: {repo.forks_count}</span>
              <span>üë§ Owner: {repo.owner.login}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StarredRepos;
