import React, { useState, useEffect, useCallback, memo } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import { API_CONFIG } from '../config/api.config';

// Extracted Repository Item Component
const RepositoryItem = memo(({ repo }) => (
  <div className="repo-item">
    <div className="repo-content">
      <div className="repo-title">
        <img
          src={repo.owner.avatar_url}
          alt={`${repo.owner.login}'s avatar`}
          className="avatar"
          loading="lazy"
        />
        <div>
          <h3>{repo.name}</h3>
          <span className="owner-name">{repo.owner.login}</span>
        </div>
      </div>
      <p className="description">
        {repo.description || 'No description available'}
      </p>
      <div className="repo-stats">
        <span className="stars">⭐ {repo.stargazers_count.toLocaleString()}</span>
      </div>
    </div>
  </div>
));

RepositoryItem.propTypes = {
  repo: PropTypes.shape({
    id: PropTypes.number.isRequired,
    name: PropTypes.string.isRequired,
    description: PropTypes.string,
    stargazers_count: PropTypes.number.isRequired,
    owner: PropTypes.shape({
      login: PropTypes.string.isRequired,
      avatar_url: PropTypes.string.isRequired,
    }).isRequired,
  }).isRequired,
};

// Footer Component
const Footer = memo(() => (
  <div className="footer-tabs">
    <a href="#trending" className="tab active">
      <span className="tab-icon">⭐</span>
      <span>Trending</span>
    </a>
    <a href="#settings" className="tab">
      <span className="tab-icon">⚙️</span>
      <span>Settings</span>
    </a>
  </div>
));

// Main Component
const StarredRepos = () => {
  const [state, setState] = useState({
    repositories: [],
    loading: true,
    page: 1,
    hasMore: true,
    isLoadingMore: false,
    error: null
  });

  const fetchRepos = useCallback(async (pageNumber) => {
    try {
      setState(prev => ({ ...prev, isLoadingMore: true, error: null }));
      
      const response = await axios.get(
        `${API_CONFIG.BASE_URL}?q=created:>${API_CONFIG.REFERENCE_DATE}&sort=stars&order=desc&page=${pageNumber}&per_page=${API_CONFIG.PER_PAGE}`
      );

      setState(prev => ({
        ...prev,
        repositories: pageNumber === 1 
          ? response.data.items 
          : [...prev.repositories, ...response.data.items],
        hasMore: response.data.items.length === API_CONFIG.PER_PAGE,
        loading: false,
        isLoadingMore: false
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: 'Failed to fetch repositories. Please try again.',
        loading: false,
        isLoadingMore: false
      }));
    }
  }, []);

  const handleScroll = useCallback(() => {
    const { loading, hasMore, isLoadingMore } = state;
    if (window.innerHeight + window.scrollY >= document.documentElement.scrollHeight - 5 
        && !loading && !isLoadingMore && hasMore) {
      setState(prev => ({ ...prev, page: prev.page + 1 }));
    }
  }, [state]);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [handleScroll]);

  useEffect(() => {
    fetchRepos(state.page);
  }, [state.page, fetchRepos]);

  const { repositories, loading, isLoadingMore, hasMore, error } = state;

  if (loading && state.page === 1) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <>
      <header className="header">
        <div className="header-content">
          <h1 className="header-title">Trending Repos</h1>
        </div>
      </header>
      
      <div className="repos-container">
        <div className="repos-list">
          {repositories.map(repo => (
            <RepositoryItem key={repo.id} repo={repo} />
          ))}
        </div>

        {isLoadingMore && (
          <div className="pagination-loader">
            <div className="loader-content">
              <span className="loader-icon">📄</span>
              <span>Loading more repositories...</span>
            </div>
          </div>
        )}

        {!hasMore && (
          <div className="pagination-end">
            <div className="end-content">
              <span className="end-icon">🎉</span>
              <span>You've reached the end!</span>
            </div>
          </div>
        )}

        <Footer />
      </div>
    </>
  );
};

export default StarredRepos;
