import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

// Add constants at the top of the file
const REFERENCE_DATE = '2024-07-15';
const API_BASE_URL = 'https://api.github.com/search/repositories';

const StarredRepos = () => {
  const [repositories, setRepositories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);

  const fetchRepos = async (pageNumber) => {
    try {
      const response = await axios.get(
        `${API_BASE_URL}?q=created:>${REFERENCE_DATE}&sort=stars&order=desc&page=${pageNumber}&per_page=30`
      );

      if (pageNumber === 1) {
        setRepositories(response.data.items);
      } else {
        setRepositories((prev) => [...prev, ...response.data.items]);
      }

      setHasMore(response.data.items.length === 30);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching repositories:', error);
      setLoading(false);
    }
  };

  const handleScroll = useCallback(() => {
    const scrollTop = document.documentElement.scrollTop;
    const scrollHeight = document.documentElement.scrollHeight;
    const clientHeight = document.documentElement.clientHeight;

    if (scrollTop + clientHeight >= scrollHeight - 5 && !loading && hasMore) {
      setPage((prevPage) => prevPage + 1);
    }
  }, [loading, hasMore]);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [handleScroll]);

  useEffect(() => {
    fetchRepos(page);
  }, [page]);

  if (loading && page === 1) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="repos-container">
      <h1>Most Starred GitHub Repositories</h1>
      <h2>(Created in the last 10 days)</h2>

      <div className="repos-list">
        {repositories.map((repo) => (
          <div key={repo.id} className="repo-item">
            <div className="repo-header">
              <img
                src={repo.owner.avatar_url}
                alt={`${repo.owner.login}'s avatar`}
                className="avatar"
              />
              <div className="repo-info">
                <h3>
                  <a
                    href={repo.html_url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {repo.name}
                  </a>
                </h3>
                <p className="owner-name">by {repo.owner.login}</p>
              </div>
            </div>
            <p className="description">
              {repo.description || 'No description available'}
            </p>
            <div className="repo-stats">
              <span className="stars">
                ‚≠ê {repo.stargazers_count.toLocaleString()} stars
              </span>
            </div>
          </div>
        ))}
      </div>

      {loading && page > 1 && <div className="loading">Loading more...</div>}

      {!hasMore && <div className="end-message">No more repositories to load.</div>}
    </div>
  );
};

export default StarredRepos;
