import React, { useState, useEffect } from 'react';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component';

const StarredRepos = () => {
  const [repositories, setRepositories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);

  const fetchRepos = async (pageNumber) => {
    try {
      const date = new Date();
      date.setDate(date.getDate() - 10);
      const dateString = date.toISOString().split('T')[0];

      const response = await axios.get(
        `https://api.github.com/search/repositories?q=created:>${dateString}&sort=stars&order=desc&page=${pageNumber}&per_page=30`
      );

      if (pageNumber === 1) {
        setRepositories(response.data.items);
      } else {
        setRepositories((prev) => [...prev, ...response.data.items]);
      }

      setHasMore(response.data.items.length === 30);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching repositories:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRepos(1);
  }, []);

  const loadMore = () => {
    const nextPage = page + 1;
    setPage(nextPage);
    fetchRepos(nextPage);
  };

  if (loading && page === 1) return <div className="loading">Loading...</div>;

  return (
    <div className="repos-container">
      <h1>Most Starred GitHub Repositories</h1>
      <h2>(Created in the last 10 days)</h2>
      <InfiniteScroll
        dataLength={repositories.length}
        next={loadMore}
        hasMore={hasMore}
        loader={<div className="loading">Loading more...</div>}
        endMessage={
          <div className="end-message">No more repositories to load.</div>
        }
      >
        {repositories.map((repo) => (
          <div key={repo.id} className="repo-item">
            <div className="repo-header">
              <img
                src={repo.owner.avatar_url}
                alt={`${repo.owner.login}'s avatar`}
                className="avatar"
              />
              <div className="repo-info">
                <h3>
                  <a
                    href={repo.html_url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {repo.name}
                  </a>
                </h3>
                <p className="owner-name">by {repo.owner.login}</p>
              </div>
            </div>
            <p className="description">
              {repo.description || 'No description available'}
            </p>
            <div className="repo-stats">
              <span className="stars">
                ‚≠ê {repo.stargazers_count.toLocaleString()} stars
              </span>
            </div>
          </div>
        ))}
      </InfiniteScroll>
    </div>
  );
};

export default StarredRepos;
